import React, { Component } from "react";
import UsersService from "./../../../service/users.service";


import "./EditForm.css";

import { Container, Row, Col, Form, Button, Spinner } from "react-bootstrap";

class EditForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: this.props.user.username,
      name: this.props.user.name,
      password: this.props.user.password,
      description: this.props.user.description,
      instrument: this.props.user.instrument,
      age: this.props.user.age,
      image: this.props.user.image,
      email: this.props.user.email,
    };
    this.usersService = new UsersService();
  }

  componentDidMount = () => {
    this.usersService
      .updateUser(this.state)
      .then((res) => this.setState({ users: res.data }))
      .catch((err) => console.log(err));
  };

  handleInputChange = (e) => this.setState({ [e.target.name]: e.target.value });

  // handleSubmit = (e) => {
  //   e.preventDefault();

  //   this.authService
  //     .signup(this.state)
  //     .then((theLoggedInUser) => {
  //       this.props.storeUser(theLoggedInUser.data);
  //       this.props.history.push("/usuarios");
  //     })
  //     .catch((err) => console.log("Error", err));
  // };

  handleImageUpload = (e) => {
    const uploadData = new FormData();
    uploadData.append("image", e.target.files[0]);
    console.log("ESTO ES UNA IMAGEN EN MEMORIA:", e.target.files[0]);

    this.setState({ uploadingActive: true });

    this.filesService
      .uploadImage(uploadData)
      .then((response) => {
        this.setState({
          user: {
            ...this.state.user,
            imageUrl: response.data.secure_url,
          },
          uploadingActive: false
        });
      })
      .catch((err) => console.log("ERRORRR!", err));
  };

  // handleSubmit = e => {
  //     e.preventDefault()

  //     this.usersService
  //         .saveUser(this.state)
  //         .then(res => {
  //             this.props.updateList()
  //         })
  //         .catch(err => console.log(err))
  // }

  render() {
    return (
      <section className="signup">
        <Container>
          <Row>
            <Col md={{ span: 6, offset: 3 }}>
              <h1>Editar perfil</h1>
              <hr />
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="username">
                  <Form.Label>Nombre de usuario</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Ej. amadeus1756"
                    name="username"
                    value={this.state.username}
                    onChange={this.handleInputChange}
                  />
                </Form.Group>
                <Form.Group controlId="name">
                  <Form.Label>Nombre y apellidos</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Ej. Wolfgang Amadeus Mozart"
                    name="name"
                    value={this.state.name}
                    onChange={this.handleInputChange}
                  />
                </Form.Group>
                <Form.Group controlId="email">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={this.state.email}
                    onChange={this.handleInputChange}
                  />
                </Form.Group>
                <Form.Group controlId="password">
                  <Form.Label>Contraseña</Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    value={this.state.password}
                    onChange={this.handleInputChange}
                  />
                </Form.Group>
                <Form.Group controlId="description">
                  <Form.Label>Descripción</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    type="text"
                    name="description"
                    value={this.state.description}
                    onChange={this.handleInputChange}
                  />
                </Form.Group>
                {/* <Form.Group controlId="instrument">
                                <Form.Label>Instrumento</Form.Label>
                                <Form.Control type="text" name="instrument" value={this.state.instrument} onChange={this.handleInputChange} />
                            </Form.Group> */}
                <Form.Group controlId="instrument">
                  <Form.Label>Instrumento</Form.Label>
                  <Form.Control
                    as="select"
                    name="instrument"
                    value={this.state.instrument}
                    onChange={this.handleInputChange}
                  >
                    <option>Seleccionar</option>
                    <option>Guitarra eléctrica</option>
                    <option>Guitarra española</option>
                    <option>Batería</option>
                    <option>Bajo</option>
                    <option>Piano</option>
                    <option>Voz</option>
                    <option>Trompeta</option>
                    <option>Acordeón</option>
                    <option>Saxofón</option>
                    <option>Trombón</option>
                    <option>Tuba</option>
                    <option>Gaita</option>
                    <option>Violín</option>
                    <option>Clarinete</option>
                    <option>Violonchelo</option>
                    <option>Contrabajo</option>
                    <option>Fagot</option>
                    <option>Ukelele</option>
                  </Form.Control>
                </Form.Group>
                <Form.Group controlId="age">
                  <Form.Label>Edad</Form.Label> <small>(Mínimo 16 años)</small>
                  <Form.Control
                    type="number"
                    name="age"
                    value={this.state.age}
                    onChange={this.handleInputChange}
                  />
                </Form.Group>
                {/* <Form.Group controlId="image">
                                <Form.Label>Imagen(URL)</Form.Label>
                                <Form.Control type="text" name="image" value={this.state.image} onChange={this.handleInputChange} />
                            </Form.Group> */}
                <Form.Group>
                  <Form.Label>
                    Imagen {this.state.uploadingActive && <Spinner />}
                  </Form.Label>
                  <Form.Control type="file" onChange={this.handleImageUpload} />
                </Form.Group>
                <Button variant="light" type="submit">
                  Guardar
                </Button>
              </Form>
            </Col>
          </Row>
        </Container>
      </section>
    );
  }
}

export default EditForm;
